package gameserver.model.templates.academy;import javax.xml.bind.annotation.XmlAccessType;import javax.xml.bind.annotation.XmlAccessorType;import javax.xml.bind.annotation.XmlAttribute;import javax.xml.bind.annotation.XmlTransient;import javax.xml.bind.annotation.XmlType;@XmlAccessorType(value=javax.xml.bind.annotation.XmlAccessType.FIELD)@XmlType(name="aloc")public class AcademySpawnTemplate{    public AcademySpawnTemplate() {        spawnId = 0;    }    public int getWorldId() {        return academySpawnGroup.getMapid();    }    public int getNpcId() {        return academySpawnGroup.getNpcid();    }    public float getX() {        return x;    }    public float getY() {        return y;    }    public float getZ() {        return z;    }    public byte getHeading() {        return heading;    }    public int getWalkerId() {        return walkerId;    }    public AcademySpawnList getSpawnGroup() {        return academySpawnGroup;    }    public void setSpawnGroup(AcademySpawnList academyspawnlist) {        academySpawnGroup = academyspawnlist;    }    public void setSpawnId(int i) {        spawnId = i;    }    public int getSpawnId() {        return spawnId;    }    @XmlTransient    private AcademySpawnList academySpawnGroup;    @XmlAttribute(name="w")    private int walkerId;    @XmlAttribute(name="h")    private byte heading;    @XmlAttribute(name="z")    private float z;    @XmlAttribute(name="y")    private float y;    @XmlAttribute(name="x")    private float x;    private int spawnId;}
